var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {

	},
	//Function to check if the date is past today
	isDatePast: function(strDate){
		//create glide datetime objects for current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// If testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate) {
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		//Extract just the date from rightnow and testdate
		today = rightnow.getLocalDate();
		istoday = testdate.getLocalDate();

		//compare today and istoday to see if they are the same date
		if (today.compateTo(isToday) == 0){
			return true;

		}
		else {
			return false;
		}
	},

	type: 'NeedItUtils'
