sysauto_script.script:
//Get right now
var rightnow = new GlideDateTime();

//Query needit task table to get the records which will be due rightnow and a day for now
var duesoon = new GlideRecord('x_58872_needit_needit_task');
duesoon.addQuery('due_date','>=', rightnow);
duesoon.addQuery('due_date','<', gs.hoursAgo(-24));
duesoon.addQuery('state', '!=', 3);
duesoon.query();

//add a message to system logs for every record
while(duesoon.next()) {
	gs.info('Sailu: The needit record is nearly due ' + duesoon.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',duesoon, duesoon.number, gs.getUserName());
}
