sysauto_script.script:
// Get right now time
var rightnow = new GlideDateTime();
//Query the needit tasks table in database to see look for overdue tasks
//Create new query
var overdue = new GlideRecord('x_58872_needit_needit_task');
overdue.addQuery('due_date', '<=', rightnow);
overdue.addQuery('state','!=',3);
overdue.query();
//For every overdue needit task, add a message in the system logs
while(overdue.next()){
	gs.info("Sailu: Overdue NeedIt task record " + overdue.number);
	gs.eventQueue('x_58872_needit.overdueNeedItTask', overdue, overdue.number, gs.getUserName());
}
